rule "WinSECAlert Stage0DropSet"
when
  // Drop noisy RapidRecovery items
  $message.winlogbeat_collector_node_id == "RapidRecoveryAccount"   OR
  // Drop actions on Machine accounts - we don't care ....right now.
  regex(".*\\$$", to_string($message.winlogbeat_event_data_TargetUserName)).matches == true OR
  // Drop Healthmailbox logins - they aren't pertinant to what we are watching.
  to_string($message.winlogbeat_event_data_TargetUserName) == "HealthMailbox*"
  
then
  //Drop the message out of processing
  drop_message();
end
