rule "AP2-WinSec-GroupMemberDelete"
when
    // assumes you have checked for
    // windows-security-information
    (
    to_string($message.winlogbeat_winlog_event_id) == "4733"  ||
    to_string($message.winlogbeat_winlog_event_id) == "4729"  ||
    to_string($message.winlogbeat_winlog_event_id) == "4757"  ||
    to_string($message.winlogbeat_winlog_event_id) == "4747"  ||
    to_string($message.winlogbeat_winlog_event_id) == "4752"  ||
    to_string($message.winlogbeat_winlog_event_id) == "4762"
    )  
    &&  
    (
       to_string($message.winlogbeat_winlog_event_data_TargetUserName)    != "None"   &&       // Rapidrecovery shenanigans
       to_string($message.winlogbeat_winlog_event_data_TargetUserName)    != "ReplayCoreEphemeralAccounts"  // 
