    // Build Alert structures
    // Create subject of (e-mail) alert
    let subject_0 = concat("GROUP MEMEBER DELETED: ", to_string($message.winlogbeat_winlog_event_data_TargetUserName));
    set_field("event_digest", subject_0);
    //
    // No detail needed - alert message structure is sufficient
