rule "AP3-WinSec-BadPw"
when
    // assumes you have checked for
    // windows-security-information
    to_string($message.winlogbeat_winlog_event_id) == "4625"             
then
    // Build Alert structures
    // Create subject of (e-mail) alert
    let subject_0 = concat("PW FAIL: ", to_string($message.winlogbeat_winlog_event_data_TargetUserName));
    let subject_1 = concat(subject_0, " connecting to ");
    let subject_fin = concat(subject_1, to_string($message.winlogbeat_winlog_host_name));
    set_field("event_digest", subject_fin);
    //
    // create detail of (e-mail) alert
    let LogonTypeNumber = to_string($message.winlogbeat_winlog_event_data_LogonType);
    let LogonTypeResult = lookup_value("winLogonType",LogonTypeNumber, 0);
    let LogonTypeErr    = lookup_value("WinLogonErr" ,to_string($message.winlogbeat_winlog_event_data_SubStatus), 0);
    let build_mess_0    = concat("Failed Password Attempt - ",  to_string($message.winlogbeat_winlog_event_data_TargetUserName));
    let build_mess_1    = concat(build_mess_0, " attempting to log in to ");
    let build_mess_2    = concat(build_mess_1, to_string($message.winlogbeat_winlog_event_SubjectDomainName));
    let build_mess_3    = concat(build_mess_2, "-");
    let build_mess_4    = concat(build_mess_3, to_string($message.winlogbeat_winlog_host_name));
    let build_mess_5    = concat(build_mess_4, ". Logon Type: ");
    let build_mess_6    = concat(build_mess_5, to_string(LogonTypeResult));
    let build_mess_7    = concat(build_mess_6, ". Attempt came from: ");
    let build_mess_8    = concat(build_mess_7, to_string($message.winlogbeat_winlog_event_data_WorkstationName));
    let build_mess_9    = concat(build_mess_8, ".  ERROR: ");
    let build_mess_fin  = concat(build_mess_9, to_string(LogonTypeErr));
    set_field("event_detail", build_mess_fin);
//    route_to_stream("P1-Alert");
//    route_to_stream("P2-Alert");
    route_to_stream("P3-Reporting");    
    route_to_stream("P3-Password");
end

