rule "WinSECAlert Stage0DropSet"
when
  // Drop noisy RapidRecovery items
  // $message.winlogbeat_winlog_collector_node_id == "RapidRecoveryAccount"                           OR    // removed, causing issues
  // Drop actions on Machine accounts - we don't care ....right now.
  regex(".*\\$$", to_string($message.winlogbeat_winlog_event_data_TargetUserName)).matches == true OR
  // Drop Healthmailbox logins - they aren't pertinant to what we are watching.
  starts_with(to_string($message.winlogbeat_winlog_event_data_TargetUserName), "HealthMailbox", true)

then
  //Drop the message out of processing
  drop_message();
end

